package fr.polytech.melusine.controllers;import fr.polytech.melusine.models.dtos.requests.AuthenticationRequest;import fr.polytech.melusine.models.dtos.responses.AuthenticationResponse;import fr.polytech.melusine.services.AuthenticationService;import org.springframework.http.HttpStatus;import org.springframework.web.bind.annotation.*;import javax.validation.Valid;@RestController@RequestMapping(path = "/auth", produces = "application/json; charset=UTF-8")public class AuthenticationController {    private final AuthenticationService authenticationService;    public AuthenticationController(AuthenticationService authenticationService) {        this.authenticationService = authenticationService;    }    @PostMapping    @ResponseStatus(HttpStatus.ACCEPTED)    public AuthenticationResponse authenticate(@RequestBody @Valid AuthenticationRequest authenticationRequest) {        return authenticationService.withPassword(authenticationRequest.getEmail(), authenticationRequest.getPassword());    }}