package fr.polytech.melusine.controllers;import fr.polytech.melusine.models.dtos.requests.AuthenticationRequest;import fr.polytech.melusine.models.dtos.responses.AuthenticationResponse;import fr.polytech.melusine.services.AuthenticationService;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@RequestMapping(path = "/auth", produces = "application/json; charset=UTF-8")public class AuthenticationController {    private final AuthenticationService authenticationService;    public AuthenticationController(AuthenticationService authenticationService) {        this.authenticationService = authenticationService;    }    @PostMapping(path = "/token")    public AuthenticationResponse authenticate(@RequestBody AuthenticationRequest authenticationRequest) {        return authenticationService.withPassword(authenticationRequest.getEmail(), authenticationRequest.getPassword());    }}